// Extract message from Discord webhook or any generic payload
const data = $json;

// Log the full incoming data
console.log('Full incoming data:', JSON.stringify(data, null, 2));

// Try multiple extraction methods
let message = '';

if (typeof data.message === 'string' && data.message.trim()) {
  message = data.message;
} else if (typeof data.content === 'string' && data.content.trim()) {
  message = data.content;
} else if (data.body && typeof data.body.message === 'string' && data.body.message.trim()) {
  message = data.body.message;
} else if (data.body && typeof data.body.content === 'string' && data.body.content.trim()) {
  message = data.body.content;
} else {
  message = 'Hello, how can I help you?';
}

// Clean and validate the message
message = String(message).trim();

// Ensure message is not empty
if (!message) {
  message = 'Hello, how can I help you?';
}

console.log('Extracted message:', message);
console.log('Message length:', message.length);

return [{ userMessage: message }];

// Create proper Cohere API request payload
const userMessage = $json.userMessage;

if (!userMessage || userMessage.trim().length === 0) {
  throw new Error('User message is empty');
}

const cohereRequest = {
  model: 'command-r-plus',
  message: userMessage.trim(),
  max_tokens: 200,
  temperature: 0.7
};

console.log('Cohere request:', JSON.stringify(cohereRequest, null, 2));
console.log('Message being sent:', cohereRequest.message);
console.log('Message length:', cohereRequest.message.length);

return [{ cohereRequest }];

// Process Cohere response
const cohereResponse = $json;
console.log('Cohere response:', JSON.stringify(cohereResponse, null, 2));

let aiResponse = '';

if (cohereResponse.text) {
  aiResponse = cohereResponse.text;
} else if (cohereResponse.error) {
  aiResponse = `⚠️ Error: ${cohereResponse.error}`;
} else if (cohereResponse.message) {
  aiResponse = cohereResponse.message;
} else {
  aiResponse = '⚠️ Unexpected response format';
}

console.log('Final AI response:', aiResponse);

return [{ response: aiResponse }];
